mapper.update:
UPDATE articles
SET title=#{title}, content=#{content}, summary=#{summary}, cover_image=#{coverImage}, 
author_id=#{authorId}, status=#{status}, views=#{views}, likes=#{likes}, comments_count=#{commentsCount}, 
is_featured=#{isFeatured}, type=#{type}, origin_url=#{originUrl}, published_at=#{publishedAt}, 
created_at=#{createdAt}, updated_at=#{updatedAt}
WHERE id=#{id}

UPDATE articles
SET likes = likes + 1
WHERE id = #{id}

UPDATE articles
SET likes = likes - 1
WHERE id = #{id}

UPDATE articles
title=#{title}, content=#{content}, summary=#{summary}, cover_image=#{coverImage}, author_id=#{authorId}, status=#{status}, views=#{views}, likes=#{likes}, comments_count=#{commentsCount}, is_featured=#{isFeatured}, type=#{type}, origin_url=#{originUrl}, published_at=#{publishedAt}, created_at=#{createdAt}, updated_at=#{updatedAt}, updated_at = NOW()
WHERE id=#{id}

UPDATE articles
SET likes = likes - 1
WHERE id = #{id}

mapper.select:
SELECT EXISTS (
    SELECT 1 FROM articles WHERE title = #{title}
)
