mapper.insert:
INSERT INTO users (
    username, email, password, avatar_url, bio, status,
    last_login, login_attempts, is_email_verified, is_active,nick_name
) VALUES (
    #{username}, #{email}, #{password}, #{avatarUrl}, #{bio}, 
    #{status}, #{lastLogin}, #{loginAttempts}, 
    #{emailVerified}, #{active}, #{nickName}
)

mapper.select:
SELECT * FROM users


        AND username LIKE CONCAT('%', #{username}, '%')


        AND email = #{email}


        AND status = #{status}

SELECT username,  nick_name, avatar_url, created_at, updated_at FROM users
ORDER BY id DESC
LIMIT #{pageSize} OFFSET #{offset}

mapper.update:
UPDATE users SET
    login_attempts = login_attempts + 1
WHERE id = #{id}

UPDATE users
    SET password = #{password} 
WHERE username = #{username}

UPDATE users
username = #{username}, email = #{email}, password = #{password}, avatar_url = #{avatarUrl}, bio = #{bio}, status = #{status}, last_login = #{lastLogin}, login_attempts = #{loginAttempts}, is_email_verified = #{emailVerified}, is_active = #{active}, nick_name = #{nickName}, updated_at = NOW()
WHERE id = #{id}
